import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import { Button } from 'reactstrap';
import { ToastContainer , toast } from  "react-toastify";

// export default (props) => {
//   return (
//     <Button color="danger">Danger!</Button>
//   );
// };

function App() {    // functional component

  const btnHandle = () => {     // Arrow Function
      toast("this is my first message");
  };

  return (
    <div>
      <ToastContainer/>           
        <h1>This is bootstrap component</h1>
        <Button color="warning" outline  onclick={btnHandle} > First React Button </Button>   
    </div>

  );
}
//  {/* ToastContainer is a self closing tag */}
// {/*  Button handle function was called here  */}
export default App;








