import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import { Button } from 'reactstrap';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';


function App(){    // functional component
  const btnHandle = () => {     // Arrow Function
      toast("this is my first message");
  };
  const notify = () => {
      toast.success("done",
      {position: "top-center"},
      );
  };

  return (
    <div>
      <ToastContainer />  {/* ToastContainer is a self closing tag */}
        <h1>This is bootstrap component</h1> 
        <Button color="success" outline onClick={notify} > First React Button </Button>
        <Button color="warning" outline onClick={btnHandle} > Second React Button </Button>   {/*  Button handle function was called here  */}     
    </div>
  );
}

export default App;

